exports.handler = function (event, context) {
    try {
        console.log("event.session.application.applicationId=" + event.session.application.applicationId);

        /**
         * Uncomment this if statement and populate with your skill's application ID to
         * prevent someone else from configuring a skill that sends requests to this function.
         */
        
        /*if (event.session.application.applicationId !== "amzn1.echo-sdk-ams.app.0d41c932-aa1f-4fe9-be87-008e145cad5f") {
             context.fail("Invalid Application ID");
        }*/
        
        if (event.session.new) {
            onSessionStarted({requestId: event.request.requestId}, event.session);
        }

        if (event.request.type === "LaunchRequest") {
            onLaunch(event.request,
                event.session,
                function callback(sessionAttributes, speechletResponse) {
                    context.succeed(buildResponse(sessionAttributes, speechletResponse));
                });
        } else if (event.request.type === "IntentRequest") {
            onIntent(event.request,
                event.session,
                function callback(sessionAttributes, speechletResponse) {
                    context.succeed(buildResponse(sessionAttributes, speechletResponse));
                });
        } else if (event.request.type === "SessionEndedRequest") {
            onSessionEnded(event.request, event.session);
            context.succeed();
        }
    } catch (e) {
        context.fail("Exception: " + e);
    }
};

/**
 * Called when the session starts.
 */
function onSessionStarted(sessionStartedRequest, session) {
    console.log("onSessionStarted requestId=" + sessionStartedRequest.requestId +
        ", sessionId=" + session.sessionId);
}

/**
 * Called when the user launches the skill without specifying what they want.
 */
function onLaunch(launchRequest, session, callback) {
    console.log("onLaunch requestId=" + launchRequest.requestId +
        ", sessionId=" + session.sessionId);

    // Dispatch to your skill's launch.
    getWelcomeResponse({}, callback);
}

/**
 * Called when the user specifies an intent for this skill.
 */
function onIntent(intentRequest, session, callback) {
    console.log("onIntent requestId=" + intentRequest.requestId +
        ", sessionId=" + session.sessionId);

    var intent = intentRequest.intent,
        intentName = intentRequest.intent.name;
    
    //List of intents you have from your intent schema
    
    if ("GetPokemon" === intentName) {
        getEventType(intent, session, callback);
    } else if ("AMAZON.HelpIntent" === intentName) {
        handleGetHelpRequest(intent, session, callback);
    } else if ("AMAZON.YesIntent" === intentName) {
        getWelcomeResponse(session, callback);
    } else if ("AMAZON.NoIntent" === intentName) {
        handleFinishSessionRequest(intent, session, callback);
    } else if ("AMAZON.StopIntent" === intentName) {
        handleFinishSessionRequest(intent, session, callback);
    } else if ("AMAZON.CancelIntent" === intentName) {
        handleFinishSessionRequest(intent, session, callback);
    } else {
        throw "Invalid intent";
    }
    
}

/**
 * Called when the user ends the session.
 * Is not called when the skill returns shouldEndSession=true.
 */
function onSessionEnded(sessionEndedRequest, session) {
    console.log("onSessionEnded requestId=" + sessionEndedRequest.requestId +
        ", sessionId=" + session.sessionId);
    // Add cleanup logic here
}

// --------------- Functions that control the skill's behavior -----------------------

function getWelcomeResponse(session, callback) {
    // If we wanted to initialize the session to have some attributes we could add those here.
    var sessionAttributes = {};
    var cardTitle = "Welcome";
    var speechOutput = "To start, launch your Alexa app and guess the name of the Pokemon you see. " +
        "Who's that Pokemon? ";
    // If the user either does not reply to the welcome message or says something that is not
    // understood, they will be prompted again with this text.
    var repromptText = "Guess the name of the Pokemon you see in the Alexa app. ";
    var shouldEndSession = false;
    
    if (session.attributes) {
        speechOutput = "Who's that Pokemon?";
    }
    
    var rngPokemon = Math.floor(Math.random() * 151) + 1; //gets a random number between 1-151
    
    sessionAttributes = {
        RNGPokemon: rngPokemon
    };
    
    callback(sessionAttributes,
        buildSpeechletResponse(cardTitle, speechOutput, repromptText, shouldEndSession, rngPokemon));
}

function handleGetHelpRequest(intent, session, callback) {
    // Provide a help prompt for the user, explaining how the game is played. Then, continue the game
    // if there is one in progress, or provide the option to start another one.
    
    var speechOutput = "To start, launch your Alexa app and guess the name of the Pokemon you see. " +
                       "Who's that Pokemon?";
    var repromptText = "Launch your Alexa app and guess the Pokemon. Who's that Pokemon? ";
    var shouldEndSession = false;
    var rngPokemon = Math.floor(Math.random() * 151) + 1; //gets a random number between 1-721
    
    //rngPokemon = 1;
    
    var sessionAttributes = {
        RNGPokemon: rngPokemon
    };
    
    callback(sessionAttributes,
        buildSpeechletResponse("Start tip", speechOutput, repromptText, shouldEndSession, rngPokemon));
}

function getEventType(intent, session, callback) {
    var shouldEndSession = false;
    var sessionAttributes = {};
    var cardTitle = intent.name;
    var repromptText = "";
    var speechOutput = "";
    var pokemonName = intent.slots.Pokemon.value;
    
    sessionAttributes = {
        rematch: true
    };
    
    if(session.attributes.RNGPokemon) {
        if (pokemonName.toLowerCase() === pokemonNames[session.attributes.RNGPokemon-1].toLowerCase()) {
            speechOutput = "Correct! It's " + pokemonNames[session.attributes.RNGPokemon-1] + ". ";
        } else {
            speechOutput = "Nope, it's " + pokemonNames[session.attributes.RNGPokemon-1] + ". ";
        }
        speechOutput += "Would you like to play again? ";
        repromptText = speechOutput;
        
        callback(sessionAttributes,
             buildSpeechletResponseNoImage(cardTitle, speechOutput, repromptText, shouldEndSession));
    } else {
        speechOutput = "To start, launch your Alexa app and guess the name of the Pokemon you see. " +
                       "Who's that Pokemon?";
        repromptText = "Launch your Alexa app and guess the Pokemon. Who's that Pokemon? ";
        shouldEndSession = false;
        var rngPokemon = Math.floor(Math.random() * 151) + 1; //gets a random number between 1-721
        
        //rngPokemon = 1;
        
        var sessionAttributes = {
            RNGPokemon: rngPokemon
        };
        
        callback(sessionAttributes,
            buildSpeechletResponse("Start tip", speechOutput, repromptText, shouldEndSession, rngPokemon)); 
        }    

}

function handleFinishSessionRequest(intent, session, callback) {
    // End the session with a "Good bye!" if the user wants to quit the game
    callback(session.attributes,
        buildSpeechletResponseWithoutCard("Good bye!", "", true));
}

// --------------- Helpers that build all of the responses -----------------------

function buildSpeechletResponse(title, output, repromptText, shouldEndSession, rngNum) {
    return {
        outputSpeech: {
            type: "PlainText",
            text: output
        },
        card: {
            type: "Standard",
            title: title,
            text: output,
            image: {
                largeImageUrl:"https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/" + rngNum + ".png"
                }
        },
        reprompt: {
            outputSpeech: {
                type: "PlainText",
                text: repromptText
            }
        },
        shouldEndSession: shouldEndSession
    };
}

function buildSpeechletResponseNoImage(title, output, repromptText, shouldEndSession, rngNum) {
    return {
        outputSpeech: {
            type: "PlainText",
            text: output
        },
        card: {
            type: "Standard",
            title: title,
            text: output
        },
        reprompt: {
            outputSpeech: {
                type: "PlainText",
                text: repromptText
            }
        },
        shouldEndSession: shouldEndSession
    };
}

function buildSpeechletResponseWithoutCard(output, repromptText, shouldEndSession) {
    return {
        outputSpeech: {
            type: "PlainText",
            text: output
        },
        reprompt: {
            outputSpeech: {
                type: "PlainText",
                text: repromptText
            }
        },
        shouldEndSession: shouldEndSession
    };
}

function buildResponse(sessionAttributes, speechletResponse) {
    return {
        version: "1.0",
        sessionAttributes: sessionAttributes,
        response: speechletResponse
    };
}